import React from 'react';
import './About.css';

function About() {
  return (
    <section className="about">
      <h2>About Us</h2>
      <p>Handiram is a traditional North Indian restaurant offering authentic, rich, and flavorful dishes inspired by classic Indian culinary heritage. Our passion for North Indian food drives us to use only the finest quality ingredients and traditional recipes.</p>
      <div className="owner-info">
        <h3>Owner: Kapil Kumar Singh</h3>
        <p>With years of experience in the culinary arts, Kapil Kumar Singh brings the essence of North Indian cuisine to your table.</p>
      </div>
    </section>
  );
}

export default About;
